configuration.tbelScript:
msgType = "POST_ATTRIBUTES_REQUEST";
msg = metadata;
metadata = {ts: msg.ts};
msg.remove('ts');
return {msg: msg, metadata: metadata, msgType: msgType};

if (msg.rcc_incr != null) {
    msg.rc_incr += msg.rcc_incr;
    msg.remove("rcc_incr");
}
//if( msg.rcc != null && msg.rc != null) {
//    msg.rc += msg.rcc
//}

return {msg: msg, metadata: metadata, msgType: msgType};

var ts = 0;
var items = ["t", "tm", "tx", "p", "h", "s", "sm", "ws", "wsx", "wsm", "wssd", "wd", "wdsd", "wdx", "rc"];
var topic = metadata.topic_base + "/";
var data = {};

if (metadata.relatedEntityId == null || metadata.ts == null) {
    topic += "error";
} else {
    topic += "d/" + metadata.relatedEntityId;
    ts = metadata.ts;
    foreach (item : items) {
        if (msg[item] != null) {
            data.put(item, msg[item]);
        }
    }
}

msg = {};
metadata = {};
if (data.size() > 0) {
    msg.put("ts", ts);
    msg.put("value", data);
    metadata.put("topic", topic);
}

return {msg: msg, metadata: metadata, msgType: msgType};

var newType = "SEND_MQTT";
// Let's specify that this is: 
// ws - weather station branch
// rtsf - RealTime State Flow processing branch
// ictp - originator is ICTP thingsboard 
//      - additional info can be added such as project
// The final topic structure should be:
// ws/rtsf/[<label1>/[...]/{d,error,cmd}/device-id
// That is, the last topic word should be the device id.
// and the previous to last word should be 'd' for data/downlink. 
// The 'error', 'cmd' are reserved for other uses.
var topic_base = "ws/rtsf/ictp";
metadata.topic_base = topic_base;
return {metadata: metadata, msgType: newType};
